sys_variable_value.value:
(function execute(inputs, outputs) {
var gr = new GlideRecord('sys_attachment');
gr.addQuery('file_name', inputs.filename);
gr.query();
var count = gr.getRowCount();
if (count > 0) {
   while (gr.next()) {
      gr.table_name = 'ZZ_YYsys_user';
      gr.file_name = 'photo';
      gr.update();
   }
}
})(inputs, outputs);

