sys_variable_value.value:
(function execute(inputs, outputs) {

var parsedResponse = JSON.parse(inputs.responsebody);

gs.info(parsedResponse.localizedLastName);
gs.info(parsedResponse.localizedFirstName);
gs.info(parsedResponse.id);
gs.info(parsedResponse.profilePicture.displayImage);

outputs.lastname = parsedResponse.localizedLastName;
outputs.firstname = parsedResponse.localizedFirstName; 
outputs.authorid = parsedResponse.id;
outputs.profilepicture = parsedResponse.profilePicture.displayImage;
})(inputs, outputs);

