sys_variable_value.value:
(function execute(inputs, outputs) {
var parsedResponse = JSON.parse(inputs.responsebody);
var myasset = parsedResponse.value.asset;
var myurl = parsedResponse.value.uploadMechanism["com.linkedin.digitalmedia.uploading.MediaUploadHttpRequest"].uploadUrl;
gs.info(myurl);
gs.info(myasset);
outputs.asset = myasset;
var fields = myurl.split('https://api.linkedin.com');
var myupload = fields[1];
gs.info(myupload);


outputs.uploadurl = myupload;
})(inputs, outputs);

