sys_variable_value.value:
(function execute(inputs, outputs) {
var parseQueryString = function() {
var str = inputs.uploadurltoparse;
    var objURL = {};

    str.replace(
        new RegExp( "([^?=&]+)(=([^&]*))?", "g" ),
        function( $0, $1, $2, $3 ){
            objURL[ $1 ] = $3;
        }
    );
    //return objURL;
gs.info(objURL.ca);
outputs.ca = objURL.ca;
outputs.cn = objURL.cn;
outputs.app = objURL.app;
outputs.m = objURL.m;
outputs.sync = objURL.sync;
outputs.v = objURL.v;
outputs.ut = objURL.ut;

gs.info(objURL.cn);
gs.info(objURL.app);
gs.info(objURL.m);
gs.info(objURL.sync);
gs.info(objURL.v);
gs.info(objURL.ut);
};

//Example how to use it: 
var params = parseQueryString(inputs.uploadurltoparse);  
var myresourcepath = inputs.uploadurltoparse;
var fields = myresourcepath.split('?');
var resourcepath = fields[0];
gs.info(resourcepath);
outputs.resourcepath = resourcepath;

})(inputs, outputs);

